  double squareratio, squareplus, squareminus, amplitudeW;
  if (fabs(m1) < fabs(m2) +fabs(m3)) {
      ///cout << "Decay not allowed as outgoing states heavier than incoming states" << endl;
      amplitudeW = 0;
    }
