  if (fabs(m1) < fabs(m2) +fabs(m3)) {
    ///cout << "Final product states heavier than initial states - decay not allowed" << endl;
    ///cout << "This is because m1= " << m1 << " m2= " << m2 << " m3= " << m3 << endl;
    amplitudeW = 0;
  }

